//SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./ERC20.sol";

contract BCCoin is ERC20 {
    constructor() ERC20("BusinessCardCoin", "BCC") {
        _mint(msg.sender, 1000000000000000000000000);
    }
}

contracts/BusinessCard.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./ERC721Enumerable.sol";
import "./access/Ownable.sol";

// Errors
error BusinessCard__NotFirstMint();
error BusinessCard__InvalidString();
error BusinessCard__InvalidETHAmountSent();

contract BusinessCard is ERC721Enumerable, Ownable {
    // State Variables
    Card[] private cards;
    uint32 internal constant MAX_CARDS = 1000;
    uint32 internal constant MAX_FIRST_MINT = 10;
    uint256 internal immutable i_firstMintPrice;
    uint256 internal immutable i_mintPrice;

    // Mappings
    mapping(address => bool) internal i_firstMinted;
    mapping(address => uint32) internal i_minted;

    struct Card {
        string name;
        string email;
        string phone;
        string company;
    }

    // Events
    event CardCreated(
        uint256 indexed id,
        string name,
        string email,
        string phone,
        string company
    );
    event CardTransfer(
        uint256 indexed id,
        address indexed from,
        address indexed to
    );

    constructor(
        uint256 firstMintPrice,
        uint256 mintPrice
    ) ERC721("BusinessCard", "BC") {
        i_firstMintPrice = firstMintPrice;
        i_mintPrice = mintPrice;
    }

    // Functions
    function mintCard(
        string memory _name,
        string memory _email,
        string memory _phone,
        string memory _company
    ) public payable returns (bool success) {
        if (
            bytes(_name).length == 0 ||
            bytes(_email).length == 0 ||
            bytes(_phone).length == 0 ||
            bytes(_company).length == 0
        ) {
            revert BusinessCard__InvalidString();
        }

        uint256 _id;

        if (i_firstMinted[msg.sender] == false) {
            if (msg.value != i_firstMintPrice) {
                revert BusinessCard__InvalidETHAmountSent();
            }
            i_firstMinted[msg.sender] = true;

            for (uint32 i = 0; i < MAX_FIRST_MINT; i++) {
                _id = cards.length;
                cards.push(Card(_name, _email, _phone, _company));
                _safeMint(msg.sender, _id);
                emit CardCreated(_id, _name, _email, _phone, _company);
            }
        } else {
            if (msg.value != i_mintPrice) {
                revert BusinessCard__InvalidETHAmountSent();
            }

            _id = cards.length;
            cards.push(Card(_name, _email, _phone, _company));
            _safeMint(msg.sender, _id);
            emit CardCreated(_id, _name, _email, _phone, _company);
        }

        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        super._transfer(from, to, tokenId);
        emit CardTransfer(tokenId, from, to);
    }

    function getCards() public view returns (Card[] memory) {
        return cards;
    }

    function getCard(uint256 _id) public view returns (Card memory) {
        return cards[_id];
    }

    function getCardCount() public view returns (uint256) {
        return cards.length;
    }

    function getCardCountByOwner(address _owner) public view returns (uint256) {
        return balanceOf(_owner);
    }

    function getCardByOwner(
        address _owner,
        uint256 _index
    ) public view returns (Card memory) {
        return cards[tokenOfOwnerByIndex(_owner, _index)];
    }
}

